import React, { useState } from 'react';
import axios from 'axios';

function ReviewForm({ fetchReviews }) {
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const review = { rating, comment };

    try {
      // Ensure the URL is pointing to the correct backend address (localhost:5000)
      await axios.post('http://localhost:5000/api/reviews', review);
      fetchReviews(); // Refresh the reviews list after submission
      setRating('');
      setComment('');
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('Error submitting review');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Rating (1-5):
        <input
          type="number"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
          min="1"
          max="5"
        />
      </label>
      <br />
      <label>
        Comment:
        <textarea
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />
      </label>
      <br />
      <button type="submit">Submit Review</button>
    </form>
  );
}

export default ReviewForm;
