import React, { useState } from 'react';
import axios from 'axios';

const ReviewForm = () => {
    const [name, setName] = useState('');
    const [rating, setRating] = useState(0);
    const [comment, setComment] = useState('');

    const submitReview = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:5000/api/reviews', { name, rating, comment });
        alert('Review submitted!');
        setName('');
        setRating(0);
        setComment('');
    };

    return (
        <form onSubmit={submitReview}>
            <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
            <input type="number" placeholder="Rating (1-5)" value={rating} onChange={(e) => setRating(e.target.value)} required />
            <textarea placeholder="Comment" value={comment} onChange={(e) => setComment(e.target.value)} required />
            <button type="submit">Submit Review</button>
        </form>
    );
};

export default ReviewForm;
